mapper.select:
select
xid, Bank, Opening, Enable, sort, Creationdate, remarks, branchid
from projects.establishment


        and xid = #{xid}


        and Bank = #{bank}


        and Opening = #{opening}


        and Enable = #{enable}


        and sort = #{sort}


        and Creationdate = #{creationdate}


        and remarks = #{remarks}


        and branchid = #{branchid}

mapper.insert:
insert into projects.establishment(Bank, Opening, Enable, sort, Creationdate, remarks, branchid) values
(#{entity.bank}, #{entity.opening}, #{entity.enable}, #{entity.sort}, #{entity.creationdate}, #{entity.remarks}, #{entity.branchid}
on duplicate key update Bank = values(Bank) , Opening = values(Opening) , Enable = values(Enable) , sort = values(sort) , Creationdate = values(Creationdate) , remarks = values(remarks) , branchid = values(branchid)
